import './App.css';
import React,{useState} from 'react';

import AceEditor from "react-ace";

import $ from 'jquery';

import "ace-builds/src-noconflict/mode-java";
import "ace-builds/src-noconflict/mode-c_cpp";
import "ace-builds/src-noconflict/mode-python";

import "ace-builds/src-noconflict/theme-github";
import "ace-builds/src-noconflict/theme-monokai";
import "ace-builds/src-noconflict/theme-solarized_dark";

import "ace-builds/src-noconflict/ext-language_tools";



function App() {
   const [lang,setLang]= useState("c_cpp");
   const [val,setVal]= useState("//cpp");
  return (
    <div>
      <div className='nav'>
      <div class="control-panel">

        <select id="languages" class="btn btn-light dropdown-toggle" onChange={chaneLang}>
            <option value="cpp"> C/C++ </option>
            <option value="java"> Java </option>
            <option value="py"> Python </option>
        </select>
    </div>
    <div class="control-panel">

        <select id="theme" class="btn btn-light dropdown-toggle" onChange={chaneLang}>
            <option value="cpp"> C/C++ </option>
            <option value="java"> Java </option>
            <option value="py"> Python </option>
        </select>
    </div>
    </div>

      <AceEditor
    mode={lang}
    width={val}
    theme="monokai"
    onChange={onChange}
    name="UNIQUE_ID_OF_DIV"
    editorProps={{ $blockScrolling: true }}
  />
   <button onClick={execute}> Run</button>
   <div class="output"></div>
    </div>
  );


  function onChange(newValue) {
    setVal(newValue);
  }
  function chaneLang(){
    let language = $("#languages").val();

    if(language === 'c' || language === 'cpp') setLang("c_cpp");
    else if(language === 'php') setLang("c_cpp");
    else if(language === 'py') setLang("python");
    else if(language === 'java') setLang("java");
  }
  function execute(){
    var _lang;
    if(lang==='c_cpp') _lang="cpp";
    $.ajax({

      url: "http://localhost:3000/",

      method: "POST",

      data: {
          language: _lang,
          code: val,
          input:""

      },

      success: function(response) {
          var obj=$(".output").text(response.output)
          obj.html(obj.html().replace(/\n/g,'<br>'));
          console.log(response.output);

      }
  })
  }
}

export default App;
